mapper.select:
SELECT ATTR_TYPE, ATTR_CODE, ATTR_VALUE, ATTR_DESC, EXT_CODE, EXT_VALUE, SORT
FROM FSIP_STATIC_PARAM WHERE 1 = 1

    and ATTR_TYPE = #{req.attrType}


    and EXT_CODE = #{req.extCode}


    and ATTR_CODE = #{req.attrCode}


    and ATTR_VALUE LIKE concat ('%',#{req.attrValue}, '%')

ORDER BY ATTR_TYPE, SORT

SELECT ATTR_CODE, ATTR_VALUE FROM FSIP_STATIC_PARAM WHERE ATTR_TYPE = #{attrType}

    and EXT_CODE = #{extCode}

ORDER BY EXT_CODE, SORT
